$.minimalTips=function(){let xOffset=20,yOffset=20,posX,posY,largura,altura,$mintip;function setPos(e){if($mintip===undefined)return;largura=$mintip.width();altura=$mintip.height();posX=((e.pageX+largura+(yOffset*2))>$(window).width())?e.pageX-(largura+50)+yOffset:e.pageX+yOffset;if((e.pageY+altura+(xOffset*2))>$(window).height()){posY=e.pageY-(altura+40)+xOffset;$mintip.removeClass('arrow-up');$mintip.addClass('arrow-down');}else{posY=e.pageY+xOffset;$mintip.removeClass('arrow-down');$mintip.addClass('arrow-up');}
$mintip.css("top",posY+"px").css("left",posX+"px").fadeIn("fast");}
$(".mintip").hover(function(e){if(this.title!==undefined&&this.title!==''){this.t=this.title;this.title="";$("body").append("<div id='MinimalTip'>"+this.t+"</div>");$mintip=$("#MinimalTip");setPos(e);}},function(){this.title=this.t;if($mintip!==undefined){$mintip.remove();}});$(".mintip").mousemove(function(e){setPos(e);});};